    X509_free(cert);
    X509_free(ca);

    /* should fail to verify now after adding in CRL */
    AssertNotNull(store = (X509_STORE *)X509_STORE_new());
    AssertNotNull((ca = wolfSSL_X509_load_certificate_file(caCert,
                           SSL_FILETYPE_PEM)));
