    AssertNull(BN_mod_inverse(&emptyBN, a, &emptyBN, NULL));
    AssertNull(BN_mod_inverse(&emptyBN, &emptyBN, m, NULL));
    AssertNull(BN_mod_inverse(&emptyBN, a, m, NULL));
