    /* good args */
    AssertNotNull(dh = PEM_read_DHparams(fp, &dh, NULL, NULL));
    XFCLOSE(fp);

    /* read in certs/dh2048.der for comparison against exported params */
    fp = XFOPEN("./certs/dh2048.der", "rb");
