    const BIGNUM *q    = NULL;
    const BIGNUM *g    = NULL;
    const BIGNUM *pub  = NULL;
    const BIGNUM *priv = NULL;
    const byte digest[WC_SHA_DIGEST_SIZE] = {0};

    AssertNotNull(dsa = DSA_generate_parameters(2048,
            NULL, 0, NULL, NULL, NULL, NULL));
